import React, { useContext } from 'react'
import { applicationContext } from '../context/ApplicationContext'

function AddTask() {
  const context = useContext(applicationContext);
  const Save = ()=>{
    var t = document.getElementById("title").value;
    var d = document.getElementById("description").value;
    var c = document.getElementById("context").value;
    context.todo.addTodo({
      title:t,
      description:d,
      context:c,
      iscompleted:false
    }).then(()=>{
      alert("Kayıt İşlemi Tamamlandı");
      document.getElementById("title").value="";
      document.getElementById("description").value="";
      document.getElementById("context").value="";
    });
  }
  return (
    <div>
      <div className='form-group'>
        <label htmlFor='title'>Başlık</label>
        <input type="text" className='form-control' id='title' name='title'/>
      </div>
      <div className='form-group'>
        <label htmlFor='description'>Açıklama</label>
        <input type="text" className='form-control' id='description' name='description'/>
      </div>
      <div className='form-group'>
        <label htmlFor='context'>İçerik</label>
        <textarea rows="3" type="text" className='form-control' id='context' name='context'/>
      </div>
      <div className='form-group'>
        <button onClick={Save} className='btn btn-success mt-1 mb-1'>Kaydet</button>
      </div>
    </div>

  )
}

export default AddTask
