import React, { createContext } from 'react'
import ReactDOM from 'react-dom'
import axios from 'axios';

var contextData={

    todo:{

        addTodo:(obj)=>{ return axios.post("httpp://localhost:3033",obj) },
        updateTodo:(id,obj)=>{return axios.put("httpp://localhost:3033"+id,obj)},
        deleteTodo:(id)=>{return axios.delete("httpp://localhost:3033"+id)},
        getAll:()=>{return axios.get("httpp://localhost:3033")},
    },


};


export const applicationContext = React.createContext(contextData);


function AppContext(props){
    return(
        <applicationContext.Provider value={contextData}>
            {props.children}
        </applicationContext.Provider>
    )
}
export default AppContext;
