import React, { useContext, useState } from "react";
import { applicationContext } from "../../context/ApplicationContext";
import Modal from "./Modal";

export default function TaskItem(props) {
  var context = useContext(applicationContext);
  const [completion, setcompletion] = useState(props.data.iscompleted)
  const [editing, setEditing] = useState(false)
  return (
    <li className="list-group-item">
      <span>
        <i className="bi bi-check"></i>
        {props.data.title}
      </span>
      <div className="btn-group">
        {props.data.iscompleted ? <button text="İncele" type="warning" onClick={()=>{}}/> : <></>}
        {!props.data.iscompleted ? <button text="Düzenle" type="warning" ismodal={true} modalid={`#modal_${props.data.id}`}/> : <></>}

        <button text={completion ? "Görev Devam Ediyor" : "Görev Bitti"} type="warning" onClick={()=>{
            setcompletion(!completion);
            context.todo.changeStatus(props.data.id);
        }}/>
        <button text="Sil" type="danger" onClick={()=>{var id = props.data.id;
            context.todo.deleteTodo(id).then(() => {
              props.changeHandler();
            });}}/>

      </div>
      <Modal changeHandler={props.changeHandler} id={`modal_${props.data.id}`} data={props.data}/>
    </li>
  );
}
